/**
 * @title GGOToken
 * @dev یک توکن ERC20 که فقط مالک آن می‌تواند توکن‌های جدید ضرب (mint) کند.
 * این قرارداد برای استفاده به عنوان توکن پاداش طراحی شده است.
 * مالکیت این قرارداد قابل انتقال است.
 */
contract GGOToken is ERC20, Ownable {
    /**
     * @dev سازنده (Constructor) قرارداد
     * نام توکن را "GGO Token" و نماد آن را "GGO" قرار می‌دهد.
     * آدرسی که این قرارداد را دیپلوی می‌کند، به عنوان مالک اولیه تعیین می‌شود.
     */
    constructor() ERC20("GGO Token", "GGO") Ownable() {}

    /**
     * @dev تابع برای ضرب (ایجاد) توکن‌های جدید.
     * فقط مالک فعلی قرارداد می‌تواند این تابع را فراخوانی کند.
     * @param to آدرسی که توکن‌های جدید را دریافت می‌کند.
     * @param amount مقدار توکنی که باید ضرب شود.
     */
    function mint(address to, uint256 amount) public onlyOwner {
        _mint(to, amount);
    }
}
